$(document).ready(function(){$(".sections-wrapper, .section").css("height",$(window).height()),$(".go_to").click(function(){var e=$(this).attr("href");return 0!=$(e).length&&$("html, body").animate({scrollTop:$(e).offset().top},500),!1});$(window).scroll(function(){$(this).scrollTop()>200?$(".header-wrapper").addClass("header-fixed-bg"):$(".header-wrapper").removeClass("header-fixed-bg")})});